package morningbatchpackage;

public class StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int a; //integer variable declaration
		
		a=10 ; //initialization
		
		String b; // String variable declaration
		          // String is  a data type & its a class
		         // String always should be initialized with in double quotes
		         // String means more than a character
		
		b="Suresh"; //String initialization
		
		System.out.print(b);
	}

}
